import React from 'react';

import {
    Button,
    Card,
    CardHeader,
    CardBody,
    CardFooter,
    Form,
    Input,
    InputGroupAddon,
    InputGroupText,
    InputGroup,
    Container,
    Col,
} from 'reactstrap';

import { history } from '../store';

// reactstrap components

// core components
import ExamplesNavbar from '../components/Navbars/ExamplesNavbar';
import TransparentFooter from '../components/Footers/TransparentFooter';

function LoginPage() {
    const [firstFocus, setFirstFocus] = React.useState(false);
    const [lastFocus, setLastFocus] = React.useState(false);
    React.useEffect(() => {
        document.body.classList.add('login-page');
        document.body.classList.add('sidebar-collapse');
        document.documentElement.classList.remove('nav-open');
        window.scrollTo(0, 0);
        document.body.scrollTop = 0;
        return function cleanup() {
            document.body.classList.remove('login-page');
            document.body.classList.remove('sidebar-collapse');
        };
    });
    return (
        <>
            <ExamplesNavbar />
            <div className="page-header clear-filter" filter-color="blue">
                <div
                    className="page-header-image"
                    style={{
                        backgroundImage: `url(${require('assets/img/login.jpg')})`,
                    }}
                />
                <div className="content">
                    <Container>
                        <Col className="ml-auto mr-auto" md="4">
                            <Card className="card-login card-plain">
                                <Form action="" className="form" method="">
                                    <CardHeader className="text-center">
                                        <div className="logo-container">
                                            <img alt="..." src={require('assets/img/now-logo.png')} />
                                        </div>
                                    </CardHeader>
                                    <CardBody>
                                        <InputGroup
                                            className={`no-border input-lg${firstFocus ? ' input-group-focus' : ''}`}>
                                            <InputGroupAddon addonType="prepend">
                                                <InputGroupText>
                                                    <i className="now-ui-icons users_circle-08" />
                                                </InputGroupText>
                                            </InputGroupAddon>
                                            <Input
                                                placeholder="First Name..."
                                                type="text"
                                                onFocus={() => setFirstFocus(true)}
                                                onBlur={() => setFirstFocus(false)}
                                            />
                                        </InputGroup>
                                        <InputGroup
                                            className={`no-border input-lg${lastFocus ? ' input-group-focus' : ''}`}>
                                            <InputGroupAddon addonType="prepend">
                                                <InputGroupText>
                                                    <i className="now-ui-icons text_caps-small" />
                                                </InputGroupText>
                                            </InputGroupAddon>
                                            <Input
                                                placeholder="Last Name..."
                                                type="text"
                                                onFocus={() => setLastFocus(true)}
                                                onBlur={() => setLastFocus(false)}
                                            />
                                        </InputGroup>
                                    </CardBody>
                                    <CardFooter className="text-center">
                                        <Button
                                            block
                                            className="btn-round"
                                            color="info"
                                            href="#pablo"
                                            onClick={e => history.push('/dashboard')}
                                            size="lg">
                                            Get Started
                                        </Button>
                                        <div className="pull-left">
                                            <h6>
                                                <a className="link" href="#pablo" onClick={e => e.preventDefault()}>
                                                    Create Account
                                                </a>
                                            </h6>
                                        </div>
                                        <div className="pull-right">
                                            <h6>
                                                <a className="link" href="#pablo" onClick={e => e.preventDefault()}>
                                                    Need Help?
                                                </a>
                                            </h6>
                                        </div>
                                    </CardFooter>
                                </Form>
                            </Card>
                        </Col>
                    </Container>
                </div>
                <TransparentFooter />
            </div>
        </>
    );
}

export default LoginPage;
